
import IWorkerChild = require('../workers/IWorkerChild');
import IWhoQuery = require('../whoIAm/IWhoQuery');
import ICollection = require('../collection/ICollection');

import IWorker = require('../workers/IWorker');

interface IService extends IWorker {
    use(worker: IWorkerChild): IService;
    inject(injector: (service: IService, use: (worker: IWorkerChild) => void) => void): IService;
    disposeWorkers(callback: () => void);
    start(callback?: (e: Error) => void);
    get(workerQuery: IWhoQuery, callback: (e: Error, results: ICollection<IWorkerChild>) => void): IService;

}

export = IService;
